+                              <div>${this.billData.phoneNumber}</div>
+                            </div>
+                          </div>
+                          <div class="to">
+                            <div class="contact">
+                              <div class="font-bold">MEPCO</div>
+                              <div>Multan Electric Power Company</div>
+                              <div>MEPCO Headquarters, Khanewal Road</div>
+                              <div>Multan, Pakistan</div>
+                            </div>
+                          </div>
+                          <div class="number text-5xl font-bold">
+                            Bill #${this.billData.referenceNumber}
+                          </div>
+                          <div class="dates">
+                            <div class="info">
+                              <div>
+                                <span>Issue Date</span> <span>${this.billData.issueDate}</span>
+                              </div>
+                              <div>
+                                <span>Due Date</span> <span>${this.billData.dueDate}</span>
+                              </div>
+                            </div>
+                          </div>
+                          <div class="note">This is your electricity consumption summary.</div>
