+              }
+
+              .wave-decoration {
+                  height: 80px;
+              }
+          }
+
+          /* Utility Classes */
+          .hidden {
+              display: none;
+          }
+
+          .animate-fade-in {
+              animation: fadeIn 0.5s ease-out;
+          }
+
+          .animate-slide-up {
+              animation: slideUp 0.5s ease-out;
+          }
+
+          @keyframes fadeIn {
+              from { opacity: 0; }
+              to { opacity: 1; }
+          }
+
+          @keyframes slideUp {
+              from { 
+                  opacity: 0;
+                  transform: translateY(20px);
+              }
+              to {
+                  opacity: 1;
+                  transform: translateY(0);
+              }
+          }
+      </style>
+  `;
+  }
+}

