@@ -0,0 +1,169 @@
+import { gsap } from "gsap";
+
+export class SystemSizing {
+  constructor(billData) {
+    this.billData = billData;
+  }
+
+  render(container) {
+    container.innerHTML = `
+            <div id="system-sizing" class="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-12 opacity-0">
+                <div class="bg-white rounded-lg shadow-sm p-6 space-y-8">
+                    <h2 class="text-2xl font-semibold text-gray-800">Recommended Solar System</h2>
+                    <div class="space-y-6">
+                        <div class="grid grid-cols-1 sm:grid-cols-3 gap-4">
+                            ${this.renderSystemSizeCard()}
+                            ${this.renderEstimatedCostCard()}
+                            ${this.renderPaybackPeriodCard()}
+                        </div>
+                        <div class="grid grid-cols-1 lg:grid-cols-2 gap-6">
+                            ${this.renderEnergyProductionSection()}
+                            ${this.renderSystemDetailsSection()}
+                        </div>
+                    </div>
+                </div>
+            </div>
+        `;
+  }
+
+  renderSystemSizeCard() {
+    return `
+            <div class="bg-gray-50 rounded-lg p-4">
+                <span class="text-sm text-gray-500">System Size</span>
+                <p class="text-lg font-semibold text-gray-800 mt-1">
+                    <span class="animate-value" data-value="${this.billData.recommendedSystemSize}" data-decimals="2">0</span> kW
+                </p>
+            </div>
+        `;
+  }
+
+  renderEstimatedCostCard() {
+    return `
+            <div class="bg-gray-50 rounded-lg p-4">
+                <span class="text-sm text-gray-500">Estimated Cost</span>
+                <p class="text-lg font-semibold text-gray-800 mt-1">
+                    $<span class="animate-value" data-value="${this.billData.estimatedSystemCost}" data-decimals="0">0</span>
+                </p>
+            </div>
+        `;
+  }
+
+  renderPaybackPeriodCard() {
+    return `
+            <div class="bg-gray-50 rounded-lg p-4">
+                <span class="text-sm text-gray-500">Payback Period</span>
+                <p class="text-lg font-semibold text-gray-800 mt-1">
+                    <span class="animate-value" data-value="${this.billData.estimatedPaybackPeriod}" data-decimals="1">0</span> years
+                </p>
+            </div>
+        `;
+  }
+
+  renderEnergyProductionSection() {
+    return `
+            <div class="bg-gray-50 rounded-lg p-4">
+                <h3 class="text-lg font-semibold text-gray-800 mb-3">Energy Production</h3>
+                <div class="grid grid-cols-2 gap-4">
+                    <div class="p-2">
+                        <span class="text-sm text-gray-500">Daily</span>
+                        <p class="text-base font-medium text-gray-800 mt-1">
+                            <span class="animate-value" data-value="${this.billData.estimatedDailyProduction}" data-decimals="2">0</span> kWh
+                        </p>
+                    </div>
+                    <div class="p-2">
+                        <span class="text-sm text-gray-500">Monthly</span>
+                        <p class="text-base font-medium text-gray-800 mt-1">
+                            <span class="animate-value" data-value="${this.billData.estimatedMonthlyProduction}" data-decimals="2">0</span> kWh
+                        </p>
+                    </div>
+                    <div class="p-2">
+                        <span class="text-sm text-gray-500">Annual</span>
+                        <p class="text-base font-medium text-gray-800 mt-1">
+                            <span class="animate-value" data-value="${this.billData.estimatedAnnualProduction}" data-decimals="2">0</span> kWh
+                        </p>
+                    </div>
+                    <div class="p-2">
+                        <span class="text-sm text-gray-500">Coverage of Needs</span>
+                        <p class="text-base font-medium text-gray-800 mt-1">
+                            <span class="animate-value" data-value="${this.billData.coveragePercentage}" data-decimals="1">0</span>%
+                        </p>
+                    </div>
+                </div>
+            </div>
+        `;
+  }
+
+  renderSystemDetailsSection() {
+    return `
+            <div class="bg-gray-50 rounded-lg p-4">
+                <h3 class="text-lg font-semibold text-gray-800 mb-3">System Details</h3>
+                <div class="grid grid-cols-2 gap-4">
+                    <div class="p-2">
+                        <span class="text-sm text-gray-500">Number of Panels</span>
+                        <p class="text-base font-medium text-gray-800 mt-1">
+                            <span class="animate-value" data-value="${this.billData.numberOfPanels}" data-decimals="0">0</span>
+                        </p>
+                    </div>
+                    <div class="p-2">
+                        <span class="text-sm text-gray-500">Panel Wattage</span>
+                        <p class="text-base font-medium text-gray-800 mt-1">${this.billData.panelWattage} W</p>
+                    </div>
+                    <div class="p-2 col-span-2">
+                        <span class="text-sm text-gray-500">Annual Savings</span>
+                        <p class="text-base font-medium text-gray-800 mt-1">
+                            $<span class="animate-value" data-value="${this.billData.estimatedAnnualSavings}" data-decimals="0">0</span>
+                        </p>
+                    </div>
+                </div>
+            </div>
+        `;
+  }
+
+  animateEntrance() {
+    return new Promise((resolve) => {
+      const timeline = gsap.timeline({
+        onComplete: resolve,
+      });
+
+      timeline.to("#system-sizing", {
+        opacity: 1,
+        duration: 0.5,
+        ease: "power2.out",
+      });
+
+      timeline.from("#system-sizing .bg-gray-50", {
+        y: 50,
+        opacity: 0,
+        duration: 0.8,
+        stagger: 0.2,
+        ease: "power3.out",
+      });
+    });
+  }
+
+  animateCountUp() {
+    const animateValue = (obj, start, end, duration) => {
+      let startTimestamp = null;
+      const step = (timestamp) => {
+        if (!startTimestamp) startTimestamp = timestamp;
+        const progress = Math.min((timestamp - startTimestamp) / duration, 1);
+        const current = progress * (end - start) + start;
+        obj.innerHTML = current.toFixed(parseInt(obj.dataset.decimals));
+        if (progress < 1) {
+          window.requestAnimationFrame(step);
+        }
+      };
+      window.requestAnimationFrame(step);
+    };
+
+    document.querySelectorAll(".animate-value").forEach((valueSpan) => {
+      const endValue = parseFloat(valueSpan.dataset.value);
+      animateValue(valueSpan, 0, endValue, 2000);
+    });
+  }
+
+  async animateAll() {
+    await this.animateEntrance();
+    this.animateCountUp();
+  }
+}

