+  cleanup() {
+    // Destroy existing charts
+    if (this.chart) {
+      this.chart.destroy();
+      this.chart = null;
+    }
+    if (this.costChart) {
+      this.costChart.destroy();
+      this.costChart = null;
+    }
+    if (this.paybackChart) {
