+import ProgressBar from "progressbar.js";
+import Chart from "chart.js/auto";
+
+export class SystemSizing {
+  constructor(billData) {
+    this.billData = billData;
+    this.chart = null;
+    this.costChart = null;
+    this.paybackChart = null;
+    this.progressBar = null;
+  }
+
+  cleanup() {
+    if (this.chart) {
+      this.chart.destroy();
+      this.chart = null;
+    }
+    if (this.costChart) {
+      this.costChart.destroy();
+      this.costChart = null;
+    }
+    if (this.paybackChart) {
+      this.paybackChart.destroy();
+      this.paybackChart = null;
+    }
+    if (this.progressBar) {
+      this.progressBar.destroy();
+      this.progressBar = null;
+    }
+  }
+
+  render(container) {
+    this.cleanup();
+
+    container.innerHTML = `
+      <div id="system-sizing" class="w-full h-full px-4 py-6 opacity-0">
