+  }
+
+  renderPaybackPeriodCard() {
+    return `
+            <div id="payback-period-card" class="bg-white rounded-lg p-4 shadow-md">
+                <h3 class="text-lg font-semibold mb-2 text-gray-800">Payback Period</h3>
