+    this.progressBar.animate(progressValue);
+  }
+
+  initEnergyProductionChart() {
+    const ctx = document.getElementById("energy-production-chart");
+    if (!ctx) return;
+
+    const monthlyData = this.generateMonthlyData();
+
+    if (this.chart) {
+      this.chart.destroy();
