+
+  initCostBreakdownChart() {
+    const ctx = document.getElementById("cost-breakdown-chart");
+    if (!ctx) return;
+
+    if (this.costChart) {
+      this.costChart.destroy();
+    }
+
+    this.costChart = new Chart(ctx, {
+      type: "doughnut",
+      data: {
+        labels: ["Equipment", "Labor", "Permits & Misc"],
