+    return (this.billData.numberOfPanels * panelArea).toFixed(0);
+  }
+
+  animateCountUp() {
+    const animateValue = (obj, start, end, duration) => {
+      let startTimestamp = null;
+      const step = (timestamp) => {
+        if (!startTimestamp) startTimestamp = timestamp;
+        const progress = Math.min((timestamp - startTimestamp) / duration, 1);
+        const current = progress * (end - start) + start;
+        obj.innerHTML = current.toFixed(parseInt(obj.dataset.decimals));
+        if (progress < 1) {
+          window.requestAnimationFrame(step);
+        }
+      };
+      window.requestAnimationFrame(step);
+    };
+
+    document.querySelectorAll(".animate-value").forEach((valueSpan) => {
+      const endValue = parseFloat(valueSpan.dataset.value);
+      animateValue(valueSpan, 0, endValue, 2000);
+    });
