+        labels: trendData.map((d) => d.month),
+        datasets: [
+          {
+            label: "Consumption (kWh)",
+            data: trendData.map((d) => d.consumption),
+            borderColor: "#3b82f6",
+            backgroundColor: "rgba(59, 130, 246, 0.1)",
+            tension: 0.4,
+            fill: true,
+            pointRadius: 6,
+            pointHoverRadius: 8,
+            pointBackgroundColor: "#ffffff",
+            pointBorderColor: "#3b82f6",
+            pointBorderWidth: 2,
+          },
+        ],
+      },
+      options: {
+        responsive: true,
+        maintainAspectRatio: false,
+        plugins: {
+          legend: {
+            display: false,
+          },
+          tooltip: {
