+        y: 0,
+        duration: 0.6,
+        stagger: 0.1,
+        ease: "power2.out",
+      }
+    );
+
+    // Animate progress bars
+    gsap.to("#co2-progress", {
+      width: "75%",
+      duration: 1.5,
+      ease: "power2.out",
+      delay: 0.5,
+    });
+
+    // Number counting animations
+    this.initializeCounters();
+  }
+
+  initializeCounters() {
+    const counters = [
