+
+      this.countUps[counter.id] = new CountUp(counter.id, counter.value, {
+        startVal: 0,
+        duration: 2,
+        useEasing: true,
+        useGrouping: true,
+        separator: counter.separator || "",
+        decimal: ".",
+        prefix: counter.prefix || "",
